/**
 * Created by Lenovo on 2019/8/4.
 */
import React from 'react';
import search from '../../images/index/search.png';
import scan from '../../images/index/scan-in.png';
import banner from '../../images/index/banner.jpg';
import './index.css';
export default class Index extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            typeList:[
                {name:'数码'},
                {name:'服装'},
                {name:'居家'},
                {name:'美妆'},
                {name:'家电'},
                {name:'玩具'},
                {name:'运动'},
            ],
            activeType:0,
            productList:[
                {productName:'衣服',productDes:"貂皮大衣",productImg:"",productPrice:"999"},
                {productName:'鞋子',productDes:"九成新的鞋子",productImg:"",productPrice:"187"},
                {productName:'数码',productDes:"",productImg:"",productPrice:""},
                {productName:'数码',productDes:"",productImg:"",productPrice:""},
            ]
        }
    }
    render() {
        return (
            <div className="cm-mlr-15">
                <div className="index-top">
                    <input type="text" className="index-input" placeholder="请输入商品关键字"/>
                    <img src={scan} alt="" className="cm-img-small"/>
                </div>
                <div>
                    <img src={banner} alt="" className="index-banner"/>
                </div>
                <div className="cm-flex cm-mtb-10 cm-ai-c">
                    {this.state.typeList.map((item,index)=>{
                        return(
                            <div className={this.state.activeType == index?"index-type-active":"index-type"} >{item.name}</div>
                        )
                    })}
                </div>
                <div className="index-product">
                    {this.state.productList.map((item,index)=>{
                        return(
                            <div>
                                <img src={item.productImg} alt=""/>
                            <div className="">{item.productImg}</div>
                            </div>
                        )
                    })}
                </div>
                <div className="index-top"></div>
            </div>
        )
    }
}
