/**
 * Created by Lenovo on 2019/8/18.
 */
import React from 'react';
import './index.css';
import {defaultProps} from './defaultProps';
export default class Type extends React.Component {
    static defaultProps = defaultProps;
    constructor(props) {
        super(props);
        this.state = {
            activeType:0//默认选中第一个类型
        }
    }
    //点击类型时触发
    onTypeClick(index,item) {
        const {typeList} =this.props;
        this.setState({
            activeType:index
        });
        var count = 0;
        var scroll = this.refs.scroll;
        var slideHalf = parseInt(this.refs.scroll.scrollWidth/2);
        console.log(slideHalf);
        if(index>3 && index<typeList.length-3){
            if(parseInt(scroll.scrollLeft)<=slideHalf){
                //setInterval(()=>{
                    scroll.scrollLeft=scroll.scrollLeft+50;
                    // if(count<=50){
                    //     console.log(scroll.scrollLeft);
                    //     scroll.scrollLeft=scroll.scrollLeft+parseInt(count*(index-3));
                    //     count+=10;
                    // }
                //},100)
            }else {
                console.log(9999999)
                scroll.scrollLeft=scroll.scrollLeft-50;
                // setInterval(()=>{
                //     if(count<=50){
                //         scroll.scrollLeft=scroll.scrollLeft-parseInt(count*(index-3));
                //         count+=10;
                //     }
                // },100)
            }
        }
        const {onTypeClick} =this.props;
        if (onTypeClick) {
            onTypeClick(item);
        }
    }
    render() {
        const {prefixCls,typeList} =this.props;
        return(
                <div className={prefixCls} ref="scroll">
                    {typeList.map((item,index)=>{
                        return(
                            <div key={index} className={this.state.activeType == index?prefixCls+"-item-active":prefixCls+"-item"} onClick={()=>this.onTypeClick(index,item)}>{item.name}</div>
                        )
                    })}
                    </div>
        )
    }
}
