/**
 * Created by Lenovo on 2019/8/18.
 */
import React from 'react';
import './index.css';
import {defaultProps} from './defaultProps';
export default class Type extends React.Component {
    static defaultProps = defaultProps;
    constructor(props) {
        super(props);
        this.state = {
            activeType:0//默认选中第一个类型
        }
    }
    //点击类型时触发
    onTypeClick(index,item) {
        this.setState({
            activeType:index
        });
        var count = 0;
        if(index<=3){
            setInterval(()=>{
                if(count<=50){
                    this.refs.scroll.scrollLeft=count;
                    count+=10;
                }
            },100)
        }
        const {onTypeClick} =this.props;
        if (onTypeClick) {
            onTypeClick(item);
        }
    }
    render() {
        const {prefixCls,typeList} =this.props;
        return(
                <div className={prefixCls} ref="scroll">
                    {typeList.map((item,index)=>{
                        return(
                            <div key={index} className={this.state.activeType == index?prefixCls+"-item-active":prefixCls+"-item"} onClick={()=>this.onTypeClick(index,item)}>{item.name}</div>
                        )
                    })}
                    </div>
        )
    }
}
