/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import '../share.css';
import './index.css';
export default class Input extends React.Component {
    constructor(props) {
        super(props);
    }
    change(event){
        const {onChange}=this.props;
        onChange(event.target.value);
    }
    render() {
        const {label,placeholder,type,src,renderRight,inputStyle,maxLength} =this.props;
        return (
            <div className="cm-flex cm-ai-c cm-jc-sb cm-width-full">
                <div className="cm-flex cm-ai-c cm-width-full">
                {src? <img src={src} alt="" className="cm-img-small-icon cm-mr-10"/>:label?<label htmlFor="" className="">{label}</label>:null}
                <input type={type?type:"text"}
                       onChange={(e)=>this.change(e)}
                       className="input-width"
                       style={inputStyle}
                       placeholder={placeholder}
                       maxLength = {maxLength}
                />
                </div>
                {renderRight?renderRight():null}
            </div>
        )
    }
}
