/**
 * Created by Lenovo on 2019/9/2.
 */
import React from 'react';
import './message.css';
import avatar from '../../images/personal/avatar.jpg';
import sale from '../../images/personal/sale.png';
import buy from '../../images/personal/buy.png';
import modify from '../../images/personal/modify.png';
import {Title} from '../../share';
import {getData, goNext, getTimeFormat, getTimeHour} from '../../utils';
//心跳检测
var heartCheck = {
    timeout: 60000, //心跳间隔时间，单位ms
    timeoutObj: null,
    serverTimeoutObj: null,
    reset: function() {
        clearTimeout(this.timeoutObj);
        clearTimeout(this.serverTimeoutObj);
        return this;
    },
    start: function() {
        var self = this;
        this.timeoutObj = setTimeout(function() {
            //这里发送一个心跳，后端收到后，返回一个心跳消息，
            //onmessage拿到返回的心跳就说明连接正常
            socket.send("heartbeat...【" + userId + "】");
            self.serverTimeoutObj = setTimeout(function() { //如果超过一定时间还没重置，说明后端主动断开了
                socket.close(); //如果onclose会执行reconnect，我们执行ws.close()就行了.如果直接执行reconnect 会触发onclose导致重连两次
            }, self.timeout)
        }, this.timeout)
    }
}
export default class MessageDetail extends React.Component {
    constructor(props) {
        super(props);
        this.item = this.props.history.location.state || {};
        console.log(this.item);
        this.count = 0;
        this.state = {
            chatList: [],
            index: 0,
            value: null
        }
        var item = this.props.history.location.state ||{};
        this.item = item;
        console.log(item);
    }

    componentDidMount() {
        this.getChatDetailList();
        var textarea = document.getElementsByTagName("textarea");
        for (var i = 0; i < textarea.length; i++) {
            textarea[i].style.height = '0.5rem';
            textarea[i].scrollTop = 0; //防抖动
            textarea[i].style.height = textarea[i].scrollHeight + 'px';
            textarea[i].addEventListener('input', function (e) {
                e.target.style.height = 'auto';
                e.target.scrollTop = 0; //防抖动
                if (e.target.scrollHeight < 20) {
                    e.target.style.height = '20px';
                } else {
                    e.target.style.height = e.target.scrollHeight + 'px';
                }
            })
        }
        var user = JSON.parse(localStorage.getItem("userInfo")) || {};
        var userId = user.userId;
        console.log(user);
        this.socket = new WebSocket("ws://49.232.24.206:9001/secondary/socket?" + userId);
        this.socket.onopen = function () {
            heartCheck.reset().start(); //心跳检测重置
            console.log("Socket 已打开");
        };
        this.socket.onmessage =  (msg)=> {
            heartCheck.reset().start(); //心跳检测重置
            if (msg.data.indexOf("{") != -1) {
                console.log(msg);
                var obj = JSON.parse(msg.data);
                this.state.chatList.push({content: obj.content,type:'received',time: getTimeHour(new Date().getTime())});
                this.setState({
                    index: this.state.index++
                })
                //console.log(obj);
            }
        };
        this.socket.onerror = function (err) {
            console.log(err);
        };
    }

    getChatDetailList() {
        getData({
            method: 'post',
            url: 'getChatDetailList',
            data: {
                chatId:this.item.chatId
            },
            successCB: (res) => {
                let user = JSON.parse(localStorage.getItem("userInfo"));
                console.log(user);
                if(res.code == 0){
                    var list = res.result.list;
                    console.log(list);
                    list.map((item)=>{
                        if(item.userId != user.userId){
                            item.type="received";
                        }
                    });
                    console.log(list);
                    this.setState({
                        chatList:list
                    })
                }
                console.log(res);
            }
        })
    }

    onLeftClick() {
        this.props.history.goBack();
    }

    sendMessage() {
        var value = this.refs.textarea.value;
        if (!value) {
            return;
        }
        this.time = new Date().getTime();
        this.state.chatList.push({content: value,time:getTimeHour(this.time)});
        this.setState({
            index: this.state.index++
        }, function () {
            this.refs.textarea.value = '';
        });
        var token = localStorage.getItem("token");
        var data = {
            "toUserId": this.item.anotherUserId||this.item.publishUserId,
            "toUserName": this.item.anotherUserName||this.item.publishUserName,
            "toUserAvatar": this.item.anotherUserAvatar||this.item.publishUserAvatar,
            "token": token,
            "content": value,
        };
        console.log(data)
        if (this.socket.readyState === 1) {
            this.socket.send(JSON.stringify(data));
        } else {
            //do something
        }
    }

    changeState(e) {
        this.setState({
            value: e.target.value
        })
    }

    choseTime() {
        var finalTime = getTimeHour(this.time);
        console.log((new Date().getTime()), (this.time + 1000 * 60));
        if ((new Date().getTime()) > (this.time + 1000 * 60)) {
            finalTime = getTimeHour(new Date().getTime())
        } else {
            if (this.count > 0) {
                finalTime = '';
            }
        }
        this.count++;
        return finalTime;
    }

    render() {
        console.log(this.state.chatList);
        var productImgs = this.item.productImgs && this.item.productImgs.split(",");
        var user = JSON.parse(localStorage.getItem("userInfo")) || {};
        return <div>
            <Title title={this.item.anotherUserName||this.item.publishUserName} onLeftClick={this.onLeftClick.bind(this)}/>
            <div className="cm-bc-white">
            <div className="cm-flex cm-jc-sb cm-p-018">
            <div className="cm-flex cm-ai-c">
                <img src={productImgs && productImgs[0]} alt="" className="cm-img-10 cm-mr-018"/>
                <div>
                    <div className="cm-c-main cm-fs-024">￥123</div>
                    <div className="cm-c-999 cm-fs-020 cm-mt-018">交易前聊一聊</div>
                </div>
            </div>
                <div className="cm-btn-border-main cm-as-fe">立即购买</div>
            </div>
            </div>
            <div>
            {this.state.chatList.map((item, index) => {
                return <div key={index} className="cm-m-018">
                    <div className="cm-flex-column" key={index}>
                        <div className="cm-tx-c cm-mtb-018">{item.time}</div>
                        {item.type == "received"? <div className="cm-flex cm-ai-c cm-tx-c cm-as-fs detail-mr-30">
                                <img src={this.item.anotherUserAvatar||this.item.publishUserAvatar} alt="" className="cm-img-06 cm-as-fs"/>
                                <div className="cm-flex">
                                    <div className="detail-triangle-right"></div>
                                    <div className="detail-chat-height cm-pad-10 cm-bc-white cm-tx-l">{item.anotherContent||item.content}</div>
                                </div>
                            </div>:
                            <div className="cm-flex cm-ai-c cm-tx-c cm-as-fe detail-ml-30">
                                <div className="cm-flex">
                                    <div className=" detail-chat-height cm-pad-10 cm-bc-ddd cm-tx-l">{item.content}</div>
                                    <div className="detail-triangle"></div>
                                </div>
                                <img src={user.userAvatar} alt="" className="cm-img-06 cm-as-fs"/>
                            </div>
                        }
                    </div>
                </div>
            })}
            </div>
            <div className="detail-position">
                <textarea onChange={(e) => this.changeState(e)} ref="textarea" className="msg-detail-textarea" name=""
                          id="" cols="30" rows="1" placeholder="想跟他说点什么呢？"></textarea>
                <span className="detail-send"
                      onClick={() => this.sendMessage()}>发送</span>
            </div>
        </div>
    }
}
