/**
 * Created by Lenovo on 2019/9/2.
 */
import React from 'react';
import './message.css';
import {Title} from '../../share';
import { PullToRefresh, ListView} from 'antd-mobile';
import ReactDOM from 'react-dom';
import {getData,getTimeFormat, getTimeHour,autoTextarea} from '../../utils';
let self;
export default class MessageDetail extends React.Component {
    constructor(props) {
        super(props);
        //获取从聊天列表页面和商品详情页传过来的参数
        this.item = this.props.history.location.state || {};
        //获取本地缓存的个人信息
        this.user = localStorage.getItem("userInfo")?JSON.parse(localStorage.getItem("userInfo")):{};
        //初始化聊天记录数据源
        const dataSource = new ListView.DataSource({
            rowHasChanged: (row1, row2) => row1 !== row2,
        });
        self = this;
        this.state = {
            dataSource,
            refreshing: true,
            hasMore:true,
            isLoading: true,
            height: document.documentElement.clientHeight,
            useBodyScroll: false,
            pageNum:1,//定义初始化请求序号
            pageSize:10,//定义初始化请求数量
            value: null,
            isShow:false
        }
        this.initData = [];
    }

    componentDidMount() {
        var textarea = document.getElementsByTagName("textarea");
        setTimeout(function () {
            autoTextarea();
        },500)

        // for (var i = 0; i < textarea.length; i++) {
        //     textarea[i].style.height = '0.6rem';
        //     textarea[i].scrollTop = 0; //防抖动
        //     textarea[i].style.height = textarea[i].scrollHeight + 'px';
        //     textarea[i].addEventListener('input', function (e) {
        //         e.target.style.height = 'auto';
        //         e.target.scrollTop = 0; //防抖动
        //         if (e.target.scrollHeight < 30) {
        //             e.target.style.height = '30px';
        //         } else {
        //             e.target.style.height = e.target.scrollHeight + 'px';
        //         }
        //     })
        // }
        this.getChatDetailList();
        var chatList = this.refs.chatList;
        chatList.addEventListener("touchstart",()=>{
            this.setState({
                isShow:false
            })
        });
        this.socket = new WebSocket("ws://49.232.24.206:9001/secondary/socket?" + this.user.userId);
        //心跳检测
        this.heartCheck = {
            timeout: 60000, //心跳间隔时间，单位ms
            timeoutObj: null,
            serverTimeoutObj: null,
            reset: function() {
                clearTimeout(this.timeoutObj);
                clearTimeout(this.serverTimeoutObj);
                return this;
            },
            start: function() {
                var that = this;
                this.timeoutObj = setTimeout(function() {
                    console.log(this);
                    //这里发送一个心跳，后端收到后，返回一个心跳消息，
                    //onmessage拿到返回的心跳就说明连接正常
                    self.socket.send("heartbeat...【" + self.user.userId + "】");
                    that.serverTimeoutObj = setTimeout(function() { //如果超过一定时间还没重置，说明后端主动断开了
                        self.socket.close(); //如果onclose会执行reconnect，我们执行ws.close()就行了.如果直接执行reconnect 会触发onclose导致重连两次
                    }, that.timeout)
                }, this.timeout)
            }
        }
        this.socket.onopen = ()=>{
            this.heartCheck.reset().start(); //心跳检测重置
            console.log("Socket 已打开");
        };
        this.socket.onmessage =  (msg)=> {
            this.heartCheck.reset().start(); //心跳检测重置
            if (msg.data.indexOf("{") != -1) {
                console.log(msg);
                var obj = JSON.parse(msg.data);
                this.initData.push({content: obj.content,type:'received',time: getTimeHour(new Date().getTime())});
                this.setState({
                    dataSource: this.state.dataSource.cloneWithRows(this.initData),
                })
            }
        };
        this.socket.onerror = function (err) {
            console.log(err);
        };
    }
    onRefresh = () => {
        if(!this.state.hasMore){
            return;
        }
        this.setState({ refreshing: true, isLoading: true });
        // simulate initial Ajax
        setTimeout(() => {
            this.getChatDetailList();
        }, 600);
    };

    onLeftClick() {
        this.props.history.goBack();
    }
    openChat(e){
        e.stopPropagation();
        this.setState({
            isShow:true
        },function () {
            autoTextarea();
        })
    }
    getChatDetailList() {
        getData({
            method: 'post',
            url: 'getChatDetailList',
            data: {
                chatId:this.item.chatId,
                pageNum:this.state.pageNum,
                pageSize:this.state.pageSize,
            },
            successCB: (res) => {
                this.state.pageNum++;
                var list = res.result.list;
                list.map((item)=>{
                    if(item.userId != this.user.userId){
                        item.type="received";
                    }
                    var nowTime = (new Date()).getTime();
                    var tomorrow = nowTime - 12*60*60*1000;
                    if(item.createTime<tomorrow){
                        item.time = getTimeFormat(item.createTime);
                    }else {
                        item.time = getTimeHour(item.createTime)
                    }
                });
                console.log(list);
                list = list.reverse();
                this.initData = list.concat(this.initData);//已有的数组拼接上拉加载更多的数组
                console.log(this.initData);
                console.dir(ReactDOM.findDOMNode(this.lv));
                const hei = this.state.height - ReactDOM.findDOMNode(this.lv).offsetTop-50;
                let total = res.result.total;//商品列表总数量
                if(total == 0){
                    // this.setState({
                    //     statusText:"暂无数据"//总数为0时表示没有数据
                    // })
                }else if(total == this.initData.length){
                    this.setState({
                        dataSource: this.state.dataSource.cloneWithRows(this.initData),
                        height: hei,
                        refreshing: false,
                        isLoading: false,
                        hasMore:false,
                        //statusText:"加载完成"//拼接的数组长度等于总数时表示加载完成
                    })
                }else {
                    this.setState({
                        dataSource: this.state.dataSource.cloneWithRows(this.initData),
                        isLoading: false,
                        refreshing: false,
                    });
                }
            }
        })
    }
    sendMessage() {
        this.setState({
            isShow: false
        });
        var value = this.refs.textarea.value;
        if (!value) {
            return;
        }
        this.time = new Date().getTime();
        this.initData.push({content: value,time:getTimeHour(this.time)});
        this.setState({
            dataSource: this.state.dataSource.cloneWithRows(this.initData),
        })
        var token = localStorage.getItem("token");
        var data = {
            "toUserId": this.item.anotherUserId||this.item.publishUserId,
            "toUserName": this.item.anotherUserName||this.item.publishUserName,
            "toUserAvatar": this.item.anotherUserAvatar||this.item.publishUserAvatar,
            "chatId":this.item.chatId,
            "token": token,
            "content": value,
        };
        if (this.socket.readyState === 1) {
            this.socket.send(JSON.stringify(data));
            //收起键盘，将高度改回来
            this.refs.textarea.blur();
            this.refs.textarea.value = "";
            this.refs.textarea.height="0.6rem";
        } else {
            //do something
        }
    }

    changeState(e) {
        this.setState({
            value: e.target.value
        })
    }
    close(){

    }
    render() {
        var productImgs = this.item.productImgs && this.item.productImgs.split(",");
        const row = (item, sectionID, rowID) => {
            return (
                <div key={rowID} className="cm-p-030">
                    {item.type == "received"?
                        <div className="cm-tx-c">
                            <div className="cm-mtb-018">
                                <span className="cm-p-006 cm-border-radius-10 cm-bc-ddd cm-c-white cm-fs-024">{item.time}</span>
                            </div>
                            <div className="cm-flex cm-ai-c cm-jc-fs">
                                <img src={this.item.anotherUserAvatar||this.item.publishUserAvatar} alt="" className="cm-img-06 cm-as-fs"/>
                                <div className="cm-flex cm-flex-1">
                                    <div className="detail-triangle-right"></div>
                                    <div className="detail-chat-height cm-bc-white cm-tx-l detail-mr-06">{item.anotherContent||item.content}</div>
                                </div>
                            </div>
                        </div>
                        :
                        <div className="cm-tx-c">
                            <div className="cm-mtb-018">
                            <span className="cm-p-006 cm-border-radius-10 cm-bc-ddd cm-c-white cm-fs-024">{item.time}</span>
                            </div>
                            <div className="cm-flex cm-ai-c cm-jc-fe">
                                <div className="cm-flex cm-flex-1 cm-jc-fe">
                                    <div className=" detail-chat-height detail-ml-06 cm-bc-main-transparent cm-c-white cm-tx-l">{item.content}</div>
                                    <div className="detail-triangle"></div>
                                </div>
                                <img src={this.user.userAvatar} alt="" className="cm-img-06 cm-as-fs"/>
                            </div>
                        </div>
                    }
                </div>
            );
        };

        return <div>
            <Title title={this.item.anotherUserName||this.item.publishUserName}
                   onLeftClick={this.onLeftClick.bind(this)}
                   style={{background:"#fff"}}
            />
            <div className="detail-fixed-height" onClick={()=>this.close()}>
            <div className="cm-flex cm-jc-sb cm-p-018 detail-fixed">
            <div className="cm-flex cm-ai-c">
                {productImgs && productImgs[0]?
                    <img src={productImgs[0]} alt="" className="cm-img-12 cm-mr-018"/>:
                    <div className="cm-img-12 cm-mr-018 cm-c-999 cm-fs-026 cm-border-ddd cm-flex cm-ai-c cm-tx-c">该商品已下架</div>
                }
                <div>
                    <div className="cm-c-main cm-fs-028 cm-fw-bold">￥123</div>
                    <div className="cm-c-999 cm-fs-026 cm-mt-018">交易前聊一聊</div>
                </div>
            </div>
                {productImgs && productImgs[0]?
                <div className="cm-btn-border-main cm-as-fe">立即购买</div>:
                    <div className="cm-btn-border-999 cm-as-fe">立即购买</div>
                }
            </div>
            </div>
            <div onClick={()=>this.close()} ref="chatList">
                <ListView
                    key={this.state.useBodyScroll ? '0' : '1'}
                    ref={el => this.lv = el}
                    dataSource={this.state.dataSource}
                    renderRow={row}
                    useBodyScroll={this.state.useBodyScroll}
                    style={this.state.useBodyScroll ? {} : {
                            height: this.state.height
                        }}
                    pullToRefresh={<PullToRefresh
                        refreshing={this.state.refreshing}
                        onRefresh={this.onRefresh}
                    />}
                    pageSize={this.state.pageSize}
                />
            </div>
            <div className="cm-bottom-position">
                <textarea onChange={(e) => this.changeState(e)}
                              ref="textarea"
                              className="cm-border-ddd cm-border-radius-10 cm-mr-018 cm-flex-1 cm-p-018" name=""
                              id="" cols="30" rows="1" placeholder="想跟他说点什么呢？"></textarea>
                <span className="cm-btn-main-higher cm-as-fe"
                      onClick={() => this.sendMessage()}>发送</span>
            </div>
        </div>
    }
}
