/**
 * Created by Lenovo on 2019/9/15.
 */
import { PullToRefresh, ListView, Button } from 'antd-mobile';
import React from 'react';
import ReactDOM from 'react-dom';
import {getData, goNext, getTimeFormat, getTimeHour,autoTextarea} from '../../utils';
const data = [
    {
        img: 'https://zos.alipayobjects.com/rmsportal/dKbkpPXKfvZzWCM.png',
        title: 'Meet hotel',
        des: '不是所有的兼职汪都需要风吹日晒',
    },
    {
        img: 'https://zos.alipayobjects.com/rmsportal/XmwCzSeJiqpkuMB.png',
        title: 'McDonald\'s invites you',
        des: '不是所有的兼职汪都需要风吹日晒',
    },
    {
        img: 'https://zos.alipayobjects.com/rmsportal/hfVtzEhPzTUewPm.png',
        title: 'Eat the week',
        des: '不是所有的兼职汪都需要风吹日晒',
    },
];
const NUM_ROWS = 20;
let pageIndex = 0;

function genData(pIndex = 0) {
    const dataArr = [];
    for (let i = 0; i < NUM_ROWS; i++) {
        dataArr.push(`row - ${(pIndex * NUM_ROWS) + i}`);
    }
    return dataArr;
}

export default class PullToRefreshScroll extends React.Component {
    constructor(props) {
        super(props);
        const dataSource = new ListView.DataSource({
            rowHasChanged: (row1, row2) => row1 !== row2,
        });
        this.user = JSON.parse(localStorage.getItem("userInfo")) || {};
        this.state = {
            dataSource,
            refreshing: true,
            hasMore:true,
            isLoading: true,
            height: document.documentElement.clientHeight,
            useBodyScroll: false,
            pageNum:1,//定义初始化请求序号
            pageSize:10,//定义初始化请求数量
        };
        this.initData = [];
    }

    componentDidUpdate() {
        if (this.state.useBodyScroll) {
            document.body.style.overflow = 'auto';
        } else {
            document.body.style.overflow = 'hidden';
        }
    }
    getChatDetailList() {
        getData({
            method: 'post',
            url: 'getChatDetailList',
            data: {
                chatId:"1fbb140527db4878a2283bca065bba15",
                pageNum:this.state.pageNum,
                pageSize:this.state.pageSize,
            },
            successCB: (res) => {
                this.state.pageNum++;
                var list = res.result.list;
                list.map((item)=>{
                    if(item.userId != this.user.userId){
                        item.type="received";
                    }
                    var nowTime = (new Date()).getTime();
                    var tomorrow = nowTime - 12*60*60*1000;
                    if(item.createTime<tomorrow){
                        item.time = getTimeFormat(item.createTime);
                    }else {
                        item.time = getTimeHour(item.createTime)
                    }
                });
                console.log(list);
                list = list.reverse();
                this.initData = list.concat(this.initData);//已有的数组拼接上拉加载更多的数组
                console.log(this.initData);
                const hei = this.state.height - ReactDOM.findDOMNode(this.lv).offsetTop;
                let total = res.result.total;//商品列表总数量
                if(total == 0){
                    // this.setState({
                    //     statusText:"暂无数据"//总数为0时表示没有数据
                    // })
                }else if(total == this.initData.length){
                    this.setState({
                        dataSource: this.state.dataSource.cloneWithRows(this.initData),
                        height: hei,
                        refreshing: false,
                        isLoading: false,
                        hasMore:false,
                        //statusText:"加载完成"//拼接的数组长度等于总数时表示加载完成
                    })
                }else {
                        this.setState({
                            dataSource: this.state.dataSource.cloneWithRows(this.initData),
                            isLoading: false,
                            refreshing: false,
                        });
                }
            }
        })
    }
    componentDidMount() {
        const hei = this.state.height - ReactDOM.findDOMNode(this.lv).offsetTop;
        this.getChatDetailList();
        // setTimeout(() => {
        //     this.rData = genData();
        // }, 1500);
    }

    onRefresh = () => {
        if (!this.state.hasMore) {
            return false;
        }
        this.setState({ refreshing: true, isLoading: true });
        // simulate initial Ajax
        setTimeout(() => {
            this.getChatDetailList();
        }, 600);
    };

    render() {
        const separator = (sectionID, rowID) => (
            <div
                key={`${sectionID}-${rowID}`}
                style={{
                    backgroundColor: '#F5F5F9',
                    height: 8,
                    borderTop: '1px solid #ECECED',
                    borderBottom: '1px solid #ECECED',
                }}
            />
        );
        let index = data.length - 1;
        const row = (item, sectionID, rowID) => {
            return (
                <div key={index} className="cm-m-018">
                    {item.type == "received"?
                        <div className="cm-flex-column">
                            <div className="cm-tx-c cm-mtb-018">{item.time}</div>
                            <div className="cm-flex cm-ai-c cm-tx-c cm-as-fs detail-mr-30">
                                <img src={this.user.userAvatar} alt="" className="cm-img-06 cm-as-fs"/>
                                <div className="cm-flex cm-flex-1">
                                    <div className="detail-triangle-right"></div>
                                    <div className="detail-chat-height cm-pad-10 cm-bc-white cm-tx-l">{item.anotherContent||item.content}</div>
                                </div>
                            </div>
                        </div>
                        :
                        <div className="cm-flex-column">
                            <div className="cm-tx-c cm-mtb-018">{item.time}</div>
                            <div className="cm-flex cm-ai-c cm-tx-c cm-as-fe detail-ml-30">
                                <div className="cm-flex cm-flex-1">
                                    <div className=" detail-chat-height cm-pad-10 cm-bc-ddd cm-tx-l">{item.content}</div>
                                    <div className="detail-triangle"></div>
                                </div>
                                <img src={this.user.userAvatar} alt="" className="cm-img-06 cm-as-fs"/>
                            </div>
                        </div>
                    }
                </div>
            );
        };
        return (<div>
            <Button
                style={{ margin: '30px 15px' }}
                inline
                onClick={() => this.setState({ useBodyScroll: !this.state.useBodyScroll })}
            >
                {this.state.useBodyScroll ? 'useBodyScroll' : 'partial scroll'}
            </Button>
            <ListView
                key={this.state.useBodyScroll ? '0' : '1'}
                ref={el => this.lv = el}
                dataSource={this.state.dataSource}
                renderRow={row}
                renderSeparator={separator}
                useBodyScroll={this.state.useBodyScroll}
                style={this.state.useBodyScroll ? {} : {
                        height: this.state.height,
                        border: '1px solid #ddd',
                        margin: '5px 0',
                    }}
                pullToRefresh={<PullToRefresh
                    refreshing={this.state.refreshing}
                    onRefresh={this.onRefresh}
                />}
                pageSize={5}
            />
        </div>);
    }
}
