/**
 * Created by Lenovo on 2019/9/22.
 */
/**
 * Created by Lenovo on 2019/9/2.
 */
import React from 'react';
import {Title,List} from '../../share';
import {goNext} from '../../utils';
import Config from '../../envConfig';
const Item = List.Item;
export default class OrderDetail extends React.Component {
    constructor(props) {
        super(props);
        //获取从聊天列表页面和商品详情页传过来的参数
        this.item = this.props.history.location.state || {};
        //获取本地缓存的个人信息
        this.user = localStorage.getItem("userInfo")?JSON.parse(localStorage.getItem("userInfo")):{};
    }
    order(){
        window.location.href = Config.serverUrl+"alipay?orderId="+this.item.orderId+"&token="+this.user.token;
    }
    render() {
        var productImgs = this.item.productImgs && this.item.productImgs.split(",")||[];
        return <div>
            <Title title="订单信息"
                   that={this}
                   style={{background:"#fff"}}
                   onLeftClick={()=>goNext(this,"index")}
            />
            <div className="cm-bc-white">
                <div className="cm-c-666 cm-pt-018 cm-tx-c">
                    <span className="cm-fs-040 cm-fw-bold">{this.item.productPrice||89}</span>
                <span>元</span>
                    <div className="cm-fs-024">未付款</div>
                </div>
                <div className="cm-tx-c cm-mtb-036 cm-fs-024"><span className="cm-c-main">0</span>天
                    <span className="cm-c-main">23</span>时
                    <span className="cm-c-main">59</span>分后，如果您未付款，订单将自动关闭
                </div>
                <div className="cm-mt-018 cm-plr-018 cm-border-bottom-eee">
                    <List border={false}>
                        <Item isNotRightIcon={true}
                            leftTitle="收货地址"
                            rightTitle={this.user.address}
                        />
                    </List>
                </div>
                <div className="cm-flex cm-jc-sb cm-bc-white cm-border-bottom-eee cm-p-018">
                    <div className="cm-flex cm-ai-c">
                        <img src={productImgs[0]} alt="" className="cm-img-05 cm-mr-018"/>
                        <div>
                            <div className="cm-c-333 cm-fs-028">{this.item.productDesc||"枇杷膏"}</div>
                        </div>
                    </div>
                    <div className="cm-c-main">联系卖家</div>
                </div>

                <div className="cm-plr-018">
                    <List border={false}>
                        <Item isNotRightIcon={true}
                            leftTitle="卖家昵称"
                            rightTitle={this.user.address}
                        />
                    </List>
                </div>
                <div className="cm-plr-018">
                    <List border={false}>
                        <Item isNotRightIcon={true}
                            leftTitle="订单编号"
                            rightTitle={this.user.address}
                        />
                    </List>
                </div>
                <div className="cm-plr-018">
                    <List border={false}>
                        <Item isNotRightIcon={true}
                            leftTitle="交易时间"
                            rightTitle={this.user.address}
                        />
                    </List>
                </div>
            </div>
            <div className="cm-bc-white">
            <div className="cm-bottom-position cm-jc-sb">
                <div className="cm-mr-018 cm-ptb-018 cm-w-full cm-tx-c cm-bc-666 cm-c-white cm-border-radius-10"
                      onClick={() => this.order()}>关闭交易</div>
                <div className="cm-ptb-018 cm-w-full cm-tx-c cm-bc-main cm-c-white cm-border-radius-10"
                      onClick={() => this.order()}>我要付款</div>
            </div>
            </div>
        </div>
    }
}
