import React from 'react';
import Index from './pages/index/index';
import myAct from './images/index/my.png';
import './App.css';
export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            children:[
                {label:"首页",icon:"",iconAct:myAct,child:Index},
                {label:"发布",icon:"",iconAct:"",child:null},
                {label:"消息",icon:"",iconAct:"",child:null},
                {label:"我的",icon:"",iconAct:"",child:null},
            ],
            activeKey:0
        }
    }
    changeTab(index){
        this.setState({
            activeKey:index
        })
    }
    render() {
        return (
            <div>
                <div className="app-tabs">
                    {this.state.children.map((item,index)=>{
                        return(<div className={index == this.state.activeKey?"app-tab-active":"app-tab"} onClick={()=>this.changeTab(index)}>
                            <img className="app-icon" src={index == this.state.activeKey?item.iconAct:item.icon} alt=""/>
                            <div>{item.label}</div>
                        </div>)
                    })}
                </div>
                {this.state.children.map((item,index)=>{
                    if(index == this.state.activeKey){
                        return(<div>{<item.child></item.child>}</div>)
                    }else {
                        return null
                    }
                })}
            </div>
        )
    }
}
