/**
 * Created by Lenovo on 2019/8/4.
 */
import React from 'react';
import praise from '../../images/index/praise.png';
import alreadyPraise from '../../images/index/already-praise.png';
import praiseHeart from '../../images/index/praise-heart.png';
import msg from '../../images/index/msg.png';
import comment from '../../images/index/comment.png';
import avatar from '../../images/index/avatar.jpg';
import {Title} from '../../share';
import {getData} from '../../utils';
import {Toast} from 'antd-mobile';
import './index.css';
export default class IndexDetail extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            praiseList: [],//点赞列表
            commentList: [],//评论列表
            isPraise:2, //是否点赞，1为是2为否，默认为2
            isShowComment:false,//是否弹出留言框
            item:{}
        };
        this.commentItem = {};
        var id = this.props.history.location.state ||{};
        this.id = id;
    }
    componentDidMount(){
        this.getProductById();
        this.getPraiseList();
        this.getCommentList();
    }
    getProductById(){
        getData({
            method: 'get',
            url: 'getProductDetail?productId='+this.id,
            successCB: (res) => {
                this.setState({
                    item: res.result
                })
            }
        })
    }
    getPraiseList(){
        getData({
            method: 'get',
            url: 'getPraiseList?productId='+this.id,
            successCB: (res) => {
                this.setState({
                    praiseList: res.result
                })
                var user = JSON.parse(localStorage.getItem("userInfo"));
                for(var i = 0;i<res.result.length;i++){
                    if(res.result[i].userName == user.userName){
                        this.setState({
                            isPraise:1
                        });
                        break;
                    }
                }
            }
        })
    }
    getCommentList(){
        getData({
            method: 'get',
            url: 'getCommentReplyList?productId='+this.id,
            successCB: (res) => {
                this.setState({
                    commentList: res.result
                })
            }
        })
    }
    showBox(commentItem){
        this.commentItem = commentItem;
        this.setState({
            isShowComment:!this.state.isShowComment
        })
    }
    comment(){
        this.setState({
            isShowComment:!this.state.isShowComment
        });
        var value = this.refs.textarea.value;
        getData({
            method: 'post',
            url: 'checkLoginValid',
            successCB: (res) => {
                getData({
                    method: 'post',
                    url: 'commentOrReply',
                    data: {
                        productId:this.id,
                        content:value,
                        toUserId:this.commentItem.fromUserId,
                        replyId: this.commentItem.id,
                    },
                    successCB: (res) => {
                        this.getCommentList()
                    }
                })
            }
        });
    }
    praise(){
        var isPraise = (this.state.isPraise == 2)?1:2;
        this.setState({
            isPraise:isPraise
        })
        getData({
            method: 'post',
            url: 'checkLoginValid',
            successCB: (res) => {
                getData({
                    method: 'post',
                    url: 'praiseOrUnPraise',
                    data: {
                        productId:this.id,
                        status:isPraise
                    },
                    successCB: (res) => {
                        this.getPraiseList();
                    }
                })
            }
        });
    }
    onLeftClick(){
        this.props.history.goBack()
    }
    render() {
        var productImgs = this.state.item.productImgs && this.state.item.productImgs.split(",")||[];
        return (
            <div>
                <Title title="详情" onLeftClick={this.onLeftClick.bind(this)}/>
                <div className="cm-mlr-15">
                    <div className="cm-flex cm-ai-c cm-border-bottom-gary cm-ptb-10 cm-fs-15">
                        <img src={this.state.item.avatar} alt="" className="cm-img-large cm-mr-10"/>
                        <div>{this.state.item.publishUserName}</div>
                    </div>
                    <div className="cm-ptb-10 cm-fs-15">
                        <div className="cm-c-main">￥{this.state.item.productPrice}</div>
                        <div className="cm-c-999 cm-mtb-10">{this.state.item.productDesc}</div>
                    </div>
                    <div>
                        {productImgs.map((img)=>{
                            return(
                                <img src={img} alt="" className="cm-img-banner cm-mt-10"/>
                                )
                        })}
                    </div>
                    <div className="cm-mtb-10">
                        <div className="detail-line">
                            <span className="cm-mr-05">点赞</span><span>{this.state.praiseList.length}</span>
                        </div>
                        <div className="cm-flex cm-ai-c cm-mtb-10">
                            <img src={praiseHeart} alt="" className="cm-img-small cm-mr-10"/>
                            <div className="cm-flex cm-ai-c">
                            {this.state.praiseList.length==0?
                                <div>暂无点赞</div>:
                                this.state.praiseList.map((item, index) => {
                                return (
                                    <div className="cm-flex cm-ai-c">
                                        <img src={item.userAvatar} className="cm-img-small cm-border-radius"/>
                                    </div>
                                )
                            })
                            }
                        </div>
                    </div>
                    </div>
                    <div className="cm-mtb-10">
                        <div className="detail-line"><span
                            className="cm-mr-05">留言</span><span>{this.state.commentList.length}</span></div>
                        <div className="cm-flex cm-ai-c cm-mtb-10">
                            <img src={msg} alt="" className="cm-img-small cm-mr-10 detail-start"/>
                            {this.state.commentList.length==0?<div>暂无留言</div>:
                                <div className="cm-width-full">
                                    {this.state.commentList.map((item, index) => {
                                    return (
                                        <div className="cm-mb-15">
                                        <div className="cm-flex cm-jc-sb cm-ai-c">
                                            <div className="cm-flex cm-ai-c">
                                                <img src={item.toUserAvatar} alt="" className="cm-img-small cm-border-radius cm-mr-10"/>
                                                <div className="cm-c-333 cm-fw-bold">{item.fromUserName}</div>
                                            </div>
                                            <div className="cm-c-999">{item.createTime}</div>
                                        </div>
                                        <div className="cm-c-999 cm-mtb-10" onClick={()=>this.showBox(item)}>{item.content}</div>
                                        </div>
                                    )
                                })}
                                </div>
                            }
                        </div>
                    </div>
                    <div className="detail-solid"></div>
                </div>
                    {this.state.isShowComment?<div className="detail-position">
                            <textarea ref="textarea" className="detail-textarea" name="" id="" cols="30" rows="6" placeholder="看对就留言，问问更多细节"></textarea>
                            <div className="detail-send" onClick={()=>this.comment()}>发送</div>
                        </div>:null}

                    <div className="cm-flex cm-ai-c cm-jc-sb detail-bottom">
                        <div className="cm-flex cm-ai-c cm-jc-sb">
                            <div className="cm-flex cm-ai-c cm-mr-10"><span className="cm-mr-05">点赞</span><img
                                src={this.state.isPraise == 1?alreadyPraise:praise} alt="" className="cm-img-small" onClick={()=>this.praise()}/></div>
                            <div className="cm-flex cm-ai-c"><span className="cm-mr-05">留言</span>
                                <img src={comment} alt="" className="cm-img-small" onClick={()=>this.showBox()}/>
                            </div>
                        </div>
                        <div className="detail-btn">聊一聊</div>
                    </div>

            </div>
        )
    }
}
