/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import {Title, List, Input,Button,Address} from '../../share';
import {getData, goNext, checkParam} from '../../utils';
import {Switch} from 'antd-mobile';
const Item = List.Item;
export default class AddAddress extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            mobile: '',
            address:'',
            detail:'',
            isShowAddress:false,
            addressList:[]
        }
        this.item = this.props.history.location.state ||{};
        if(this.item.consigneeName){
            this.state.consigneeName = this.item.consigneeName;
            this.state.consigneeMobile = this.item.consigneeMobile;
            this.state.consigneeAddress = this.item.consigneeAddress;
            this.state.addressDetail = this.item.addressDetail;
            this.state.isDefaultAddress = this.item.isDefaultAddress === 2?true:false;
        }
    }
    change(state) {
        this.setState(state)
    }
    login() {
        var arr = [
            {value: this.state.mobile, msg: "请输入手机号"},
            {value: this.state.password, msg: "请输入密码"},
        ]
        checkParam(arr, () => {
            getData({
                method: 'post',
                url: 'login',
                data: {
                    mobile: this.state.mobile,
                    password: this.state.password,
                },
                successCB: (res) => {
                    var token = res.result.token;
                    //登录成功之后将个人信息缓存到本地，并进入首页
                    localStorage.setItem("userInfo", JSON.stringify(res.result));
                    localStorage.setItem("token", token);
                    goNext(this, 'index')
                }
            })
        })
    }
    showAddressModel(){
        this.setState({
            isShowAddress:true
        })
    }
    closeAddressModel(){
        this.setState({
            isShowAddress:false
        });
    }
    getAddress(address){
        this.setState({
            isShowAddress:false,
            addressList:address
        });
    }
    render() {
        return (
            <div>
                <Title that={this} title={this.item.consigneeName?"编辑收货地址":"添加收货地址"}/>
                <div className="cm-mlr-018">
                    <List>
                        <Input
                            placeholder="收货人"
                            defaultValue={this.state.consigneeName}
                            onChange={(val) => this.change({consigneeName: val})}
                            style={{textAlign:'left'}}
                        />
                    </List>
                    <List>
                        <Input
                            type="number"
                            placeholder="手机号码"
                            defaultValue={this.state.consigneeMobile}
                            onChange={(val) => this.change({consigneeMobile: val})}
                            style={{textAlign:'left'}}
                        />
                    </List>
                    <List onClick={()=>this.showAddressModel()}>
                        <Item
                            leftTitle={
                                this.state.addressList.length>0?
                                this.state.addressList.map((item,index)=>{
                                   return <div key={index}>{item}</div>
                                }) :"所在地区"
                            }
                        />
                    </List>
                    <List>
                        <Input
                            type="text"
                            placeholder="详细地址：如建中路88号8层808室"
                            defaultValue={this.state.addressDetail}
                            onChange={(val) => this.change({addressDetail: val})}
                            style={{textAlign:'left'}}
                        />
                    </List>
                    <div className="cm-flex cm-ptb-018 cm-jc-sb">
                        <div>设为默认地址</div>
                        <Switch
                            checked={this.state.isDefaultAddress}
                            onChange={() => {
                                this.setState({
                                    isDefaultAddress: !this.state.isDefaultAddress,
                                });
                            }}
                        />
                    </div>
                    {this.item.consigneeName?<div className="cm-flex cm-jc-sb"><Button type="half" className="cm-mt-080" onClick={() => this.login()}>保存</Button>
                            <Button type="half"  className="cm-mt-080" onClick={() => this.login()}>删除</Button>
                        </div>: <Button type="fill"  className="cm-mt-080" onClick={() => this.login()}>保存</Button>}

                </div>
                {this.state.isShowAddress?<Address
                        closeModel={()=>this.closeAddressModel()}
                        getAddress={(address)=>this.getAddress(address)}
                    />:null}
            </div>)
    }
}
